clc, clear
img = imread('textimg.jpg');
[rows, cols, ~] = size(img);
rows = round(rows./3);
p1 = mat2gray(rgb2gray(img(1:rows, :, :)));
p1 = cat(3, p1, p1, p1);

p2 = mat2gray(img(rows:rows.*2, :, :));

p3 = mat2gray(im2bw(img(rows.*2:rows.*3-1, :, :)));
p3 = cat(3, p3, p3, p3);

res = cat(1,p1, p2, p3);
imshow(res);




                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             

